import React, { useState } from "react";

export default function Cart({
  items = [],
  updateQty,
  removeItem,
  clearCart,
  placeOrder,
}) {
  const [customer, setCustomer] = useState({
    name: "",
    email: "",
    note: "",
  });

  const total = items.reduce((sum, i) => sum + i.price * i.qty, 0);

  function handleSubmit(e) {
    e.preventDefault();
    if (!customer.name || !customer.email) {
      alert("Please enter your name and email.");
      return;
    }
    placeOrder(customer);
  }

  return (
    <section className="container">
      <h2>Your Cart</h2>

      {items.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          {/* Top action bar */}
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              marginBottom: "1rem",
            }}
          >
            <button
              onClick={clearCart}
              style={{
                background: "#b30000",
                color: "white",
                border: "none",
                padding: "0.6rem 1rem",
                borderRadius: "6px",
                cursor: "pointer",
              }}
            >
              Remove All Items
            </button>

            <button
              onClick={() => (window.location.hash = "#menu")}
              style={{
                background: "#555",
                color: "white",
                border: "none",
                padding: "0.6rem 1rem",
                borderRadius: "6px",
                cursor: "pointer",
              }}
            >
              Continue Shopping
            </button>
          </div>

          {/* Cart table */}
          <table className="cart-table">
            <thead>
              <tr>
                <th>Item</th>
                <th>Image</th>
                <th>Price</th>
                <th>Qty</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {items.map((it) => (
                <tr key={it.item}>
                  <td>{it.name}</td>

                  {/* Item image */}
                  <td>
                    <img
                      src={it.image || "/hero.jpg"}
                      alt={it.name}
                      style={{
                        width: "60px",
                        height: "60px",
                        objectFit: "cover",
                        borderRadius: "6px",
                      }}
                    />
                  </td>

                  <td>${it.price.toFixed(2)}</td>

                  {/* Qty controls */}
                  <td>
                    <div
                      style={{
                        display: "flex",
                        alignItems: "center",
                        gap: "0.5rem",
                      }}
                    >
                      <button
                        onClick={() =>
                          updateQty(it.item, Math.max(1, it.qty - 1))
                        }
                        style={{
                          width: "28px",
                          height: "28px",
                          fontSize: "18px",
                          borderRadius: "50%",
                          border: "1px solid #ccc",
                          background: "white",
                          cursor: "pointer",
                        }}
                      >
                        â€“
                      </button>

                      <span>{it.qty}</span>

                      <button
                        onClick={() => updateQty(it.item, it.qty + 1)}
                        style={{
                          width: "28px",
                          height: "28px",
                          fontSize: "18px",
                          borderRadius: "50%",
                          border: "1px solid #ccc",
                          background: "white",
                          cursor: "pointer",
                        }}
                      >
                        +
                      </button>
                    </div>
                  </td>

                  <td>
                    <button
                      onClick={() => removeItem(it.item)}
                      style={{
                        background: "#c0392b",
                        color: "white",
                        border: "none",
                        padding: "0.4rem 0.8rem",
                        borderRadius: "6px",
                        cursor: "pointer",
                      }}
                    >
                      Remove
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* Cart summary */}
          <div className="cart-summary" style={{ marginTop: "1rem" }}>
            <p>
              Total: <strong>${total.toFixed(2)}</strong>
            </p>
          </div>

          {/* Checkout Form */}
          <form onSubmit={handleSubmit} className="order-form">
            <h3>Checkout</h3>

            <input
              placeholder="Name"
              value={customer.name}
              onChange={(e) =>
                setCustomer({ ...customer, name: e.target.value })
              }
              required
            />
            <input
              placeholder="Email"
              type="email"
              value={customer.email}
              onChange={(e) =>
                setCustomer({ ...customer, email: e.target.value })
              }
              required
            />
            <textarea
              placeholder="Note (optional)"
              value={customer.note}
              onChange={(e) =>
                setCustomer({ ...customer, note: e.target.value })
              }
            />

            <button
              type="submit"
              style={{
                background: "green",
                color: "white",
                padding: "0.7rem 1rem",
                border: "none",
                borderRadius: "6px",
                cursor: "pointer",
                fontSize: "1rem",
              }}
            >
              Checkout
            </button>
          </form>
        </>
      )}
    </section>
  );
}
