import React, { useState } from 'react';

export default function Cart({ items = [], updateQty, removeItem, placeOrder }) {
  const [customer, setCustomer] = useState({ name: '', email: '', note: '' });

  const total = items.reduce((s, i) => s + i.price * i.qty, 0);

  function handleOrderSubmit(e) {
    e.preventDefault();
    if (!customer.name || !customer.email) {
      alert('Please enter your name and email.');
      return;
    }
    placeOrder(customer);
  }

  function clearCart() {
    if (window.confirm("Remove all items from cart?")) {
      items.forEach(it => removeItem(it.item));
    }
  }

  return (
    <section className="container">
      <h2>Your Cart</h2>

      {items.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          <table className="cart-table">
            <thead>
              <tr>
                <th>Item</th>
                <th>Qty</th>
                <th>Price</th>
                <th></th>
              </tr>
            </thead>

            <tbody>
              {items.map(it => (
                <tr key={it.item}>
                  <td>{it.name}</td>

                  {/* Quantity Buttons */}
                  <td>
                    <div style={{ display: 'flex', alignItems: 'center', gap: '6px' }}>
                      <button 
                        className="qty-btn"
                        onClick={() => updateQty(it.item, it.qty - 1)}
                        disabled={it.qty <= 1}
                      >
                        â€“
                      </button>

                      <span>{it.qty}</span>

                      <button
                        className="qty-btn"
                        onClick={() => updateQty(it.item, it.qty + 1)}
                      >
                        +
                      </button>
                    </div>
                  </td>

                  <td>${(it.price * it.qty).toFixed(2)}</td>

                  <td>
                    <button className="remove-btn" onClick={() => removeItem(it.item)}>
                      Remove
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* Total + Buttons */}
          <div className="cart-summary" style={{ marginTop: "1rem" }}>
            <p style={{ fontSize: "1.2rem" }}>
              Total: <strong>${total.toFixed(2)}</strong>
            </p>

            <div style={{ display: "flex", gap: "1rem" }}>
              <button className="clear-btn" onClick={clearCart}>
                Clear Cart
              </button>

              <button
                className="checkout-btn"
                onClick={() => document.getElementById("checkout-form").scrollIntoView({ behavior: "smooth" })}
              >
                Checkout
              </button>
            </div>
          </div>

          {/* Checkout Form */}
          <form id="checkout-form" onSubmit={handleOrderSubmit} style={{ marginTop: "2rem" }}>
            <h3>Checkout</h3>

            <input
              placeholder="Name"
              value={customer.name}
              onChange={e => setCustomer({ ...customer, name: e.target.value })}
              required
            />

            <input
              placeholder="Email"
              type="email"
              value={customer.email}
              onChange={e => setCustomer({ ...customer, email: e.target.value })}
              required
            />

            <textarea
              placeholder="Order Notes (optional)"
              value={customer.note}
              onChange={e => setCustomer({ ...customer, note: e.target.value })}
            />

            <button type="submit" className="place-order-btn">
              Place Order
            </button>
          </form>
        </>
      )}
    </section>
  );
}
