import React, { useState } from 'react';

export default function Cart({ items = [], updateQty, removeItem, placeOrder }) {
  const [customer, setCustomer] = useState({ name: '', email: '', note: '' });

  const total = items.reduce((s,i)=> s + i.price * i.qty, 0);

  function submit(e) {
    e.preventDefault();
    if (!customer.name || !customer.email) {
      alert('Please enter name and email');
      return;
    }
    placeOrder(customer);
  }

  return (
    <section className="container">
      <h2>Your Cart</h2>
      {items.length === 0 ? <p>Your cart is empty.</p> : (
        <>
          <table className="cart-table">
            <thead>
              <tr><th>Item</th><th>Price</th><th>Qty</th><th></th></tr>
            </thead>
            <tbody>
              {items.map(it => (
                <tr key={it.item}>
                  <td>{it.name}</td>
                  <td>${it.price.toFixed(2)}</td>
                  <td>
                    <input type="number" min="1" value={it.qty} onChange={(e)=>updateQty(it.item, parseInt(e.target.value||1))} />
                  </td>
                  <td><button onClick={()=>removeItem(it.item)}>Remove</button></td>
                </tr>
              ))}
            </tbody>
          </table>
          <div className="cart-summary">
            <p>Total: <strong>${total.toFixed(2)}</strong></p>
          </div>
          <form onSubmit={submit} className="order-form">
            <h3>Place Order</h3>
            <input placeholder="Name" value={customer.name} onChange={e=>setCustomer({...customer, name: e.target.value})} required />
            <input placeholder="Email" value={customer.email} onChange={e=>setCustomer({...customer, email: e.target.value})} required />
            <textarea placeholder="Note" value={customer.note} onChange={e=>setCustomer({...customer, note: e.target.value})} />
            <button type="submit">Place Order</button>
          </form>
        </>
      )}
    </section>
  );
}
