import React, { useState } from "react";

export default function Cart({ items = [], updateQty, removeItem, placeOrder }) {
  const [customer, setCustomer] = useState({ name: "", email: "", note: "" });

  const total = items.reduce((sum, i) => sum + i.price * i.qty, 0);

  function submit(e) {
    e.preventDefault();
    if (!customer.name || !customer.email) {
      alert("Please enter your name and email to checkout.");
      return;
    }
    placeOrder(customer);
  }

  function clearCart() {
    if (window.confirm("Remove all items from cart?")) {
      items.forEach((i) => removeItem(i.item));
    }
  }

  return (
    <section className="container">
      <h2>Your Cart</h2>

      {items.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          <table className="cart-table">
            <thead>
              <tr>
                <th>Item</th>
                <th>Image</th>
                <th>Price</th>
                <th>Qty</th>
                <th></th>
              </tr>
            </thead>

            <tbody>
              {items.map((it) => (
                <tr key={it.item}>
                  <td>{it.name}</td>

                  {/* IMAGE COLUMN */}
                  <td>
                    <img
                      src={it.image || "/hero.jpg"}
                      alt={it.name}
                      style={{ width: "60px", height: "60px", borderRadius: "6px", objectFit: "cover" }}
                    />
                  </td>

                  <td>${it.price.toFixed(2)}</td>

                  {/* + / - BUTTONS */}
                  <td>
                    <div style={{ display: "flex", alignItems: "center", gap: "6px" }}>
                      <button
                        onClick={() => updateQty(it.item, it.qty - 1)}
                        disabled={it.qty === 1}
                        style={{ padding: "4px 8px" }}
                      >
                        âˆ’
                      </button>

                      <span>{it.qty}</span>

                      <button
                        onClick={() => updateQty(it.item, it.qty + 1)}
                        style={{ padding: "4px 8px" }}
                      >
                        +
                      </button>
                    </div>
                  </td>

                  {/* REMOVE BUTTON */}
                  <td>
                    <button onClick={() => removeItem(it.item)}>Remove</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* CART TOTAL */}
          <div className="cart-summary">
            <p>
              Total: <strong>${total.toFixed(2)}</strong>
            </p>
          </div>

          {/* REMOVE ALL BUTTON */}
          <button
            onClick={clearCart}
            style={{
              background: "#555",
              color: "white",
              padding: "0.6rem 1rem",
              border: "none",
              borderRadius: "6px",
              marginBottom: "1rem",
              cursor: "pointer",
            }}
          >
            Remove All Items
          </button>

          {/* CHECKOUT FORM */}
          <form onSubmit={submit} className="order-form">
            <h3>Checkout</h3>

            <input
              placeholder="Full Name"
              value={customer.name}
              onChange={(e) => setCustomer({ ...customer, name: e.target.value })}
              required
            />

            <input
              placeholder="Email"
              type="email"
              value={customer.email}
              onChange={(e) => setCustomer({ ...customer, email: e.target.value })}
              required
            />

            <textarea
              placeholder="Any notes about your order"
              value={customer.note}
              onChange={(e) => setCustomer({ ...customer, note: e.target.value })}
            />

            <button
              type="submit"
              style={{
                background: "#c0392b",
                color: "white",
                padding: "0.7rem 1.2rem",
                border: "none",
                borderRadius: "6px",
                cursor: "pointer",
                marginTop: "10px",
              }}
            >
              Checkout
            </button>
          </form>
        </>
      )}
    </section>
  );
}
