import React from 'react';

export default function About() {
  return (
    <section className="container">
      <h2>About Us</h2>
      <p>
        <strong>Our Story:</strong> Founded in 2010, My Restaurant began as a small family pizzeria. Over the years we've grown into a neighborhood favorite while sticking to our mission: fresh ingredients, passion for craft, and service with a smile.
      </p>
      <p>
        <strong>Special Dishes:</strong> Our signature Margherita Pizza and Tiramisu are house favorites. We source produce locally and make sauces and desserts in-house daily.
      </p>
      <p>
        <strong>Mission:</strong> Create welcoming meals for the community where people gather to enjoy simple, honest food.
      </p>
    </section>
  );
}
