import React, { useState } from 'react';

export default function Cart({
  items = [],
  incrementQty,
  decrementQty,
  updateQty,
  removeItem,
  clearCart,
  placeOrder
}) {
  const [customer, setCustomer] = useState({ name: '', email: '', note: '' });

  const total = items.reduce((s,i)=> s + i.price * i.qty, 0);

  function submit(e) {
    e.preventDefault();
    if (!customer.name || !customer.email) {
      alert('Please enter name and email');
      return;
    }
    placeOrder(customer);
  }

  return (
    <section className="container">
      <h2>Your Cart</h2>

      {items.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          <div className="cart-actions-row" style={{display:'flex',alignItems:'center',justifyContent:'space-between', marginBottom:'1rem'}}>
            <div>
              <button className="btn-secondary" onClick={clearCart}>Remove all items</button>
            </div>
            <div>
              <button className="btn-primary" onClick={() => {
                // navigate to checkout section (if you have one) -- for now open order form below
                const el = document.querySelector('.order-form');
                if (el) el.scrollIntoView({ behavior: 'smooth', block: 'start' });
              }}>Checkout</button>
            </div>
          </div>

          <table className="cart-table" style={{width:'100%'}}>
            <thead>
              <tr>
                <th style={{width:'80px'}}>Image</th>
                <th>Item</th>
                <th>Price</th>
                <th style={{width:'140px'}}>Quantity</th>
                <th>Subtotal</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {items.map(it => (
                <tr key={it.item}>
                  <td>
                    <img src={it.image || '/hero.jpg'} alt={it.name} style={{width:70,height:50,objectFit:'cover',borderRadius:6}} />
                  </td>
                  <td>{it.name}</td>
                  <td>${(it.price||0).toFixed(2)}</td>
                  <td>
                    <div style={{display:'flex',alignItems:'center',gap:'0.5rem'}}>
                      <button onClick={()=>decrementQty(it.item)} aria-label="decrement" className="qty-btn">âˆ’</button>
                      <input
                        type="number"
                        min="1"
                        value={it.qty}
                        onChange={(e)=> {
                          const v = parseInt(e.target.value || 1, 10);
                          updateQty(it.item, Math.max(1, v));
                        }}
                        style={{width:60, textAlign:'center', padding:'6px', borderRadius:6, border:'1px solid #ddd'}}
                      />
                      <button onClick={()=>incrementQty(it.item)} aria-label="increment" className="qty-btn">+</button>
                    </div>
                  </td>
                  <td>${(it.price * it.qty).toFixed(2)}</td>
                  <td>
                    <button onClick={()=>removeItem(it.item)} className="btn-link">Remove</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <div className="cart-summary" style={{display:'flex', justifyContent:'space-between', alignItems:'center', marginTop:'1rem'}}>
            <p style={{fontSize:'1.1rem'}}>Total: <strong>${total.toFixed(2)}</strong></p>
            <div>
              <button className="btn-secondary" onClick={clearCart}>Remove all</button>
              <button className="btn-primary" style={{marginLeft:'0.75rem'}} onClick={() => {
                const el = document.querySelector('.order-form');
                if (el) el.scrollIntoView({ behavior: 'smooth', block: 'start' });
              }}>Checkout</button>
            </div>
          </div>

          <form onSubmit={submit} className="order-form" style={{marginTop:'1.5rem'}}>
            <h3>Place Order</h3>
            <input placeholder="Name" value={customer.name} onChange={e=>setCustomer({...customer, name: e.target.value})} required />
            <input placeholder="Email" value={customer.email} onChange={e=>setCustomer({...customer, email: e.target.value})} required />
            <textarea placeholder="Note" value={customer.note} onChange={e=>setCustomer({...customer, note: e.target.value})} />
            <div style={{display:'flex', gap:'0.75rem', marginTop:'0.5rem'}}>
              <button type="submit" className="btn-primary">Place Order</button>
              <button type="button" className="btn-secondary" onClick={()=>clearCart()}>Clear Cart</button>
            </div>
          </form>
        </>
      )}
    </section>
  );
}
